import{_ as e,p as r,q as s,a1 as i}from"./framework-204010b2.js";const l={},t=i('<h1 id="risotto-aux-asperges-vertes" tabindex="-1"><a class="header-anchor" href="#risotto-aux-asperges-vertes" aria-hidden="true">#</a> Risotto aux asperges vertes</h1><p>Recette de Cyril Lignac dans l&#39;émission &quot;Tous en cuisine&quot; du 19 mai 2020.</p><p><img src="https://pbs.twimg.com/media/EYYfv93XsAAjj2-?format=jpg&amp;name=4096x4096" alt="Risotto aux asperges vertes"></p><h2 id="ingredients" tabindex="-1"><a class="header-anchor" href="#ingredients" aria-hidden="true">#</a> Ingrédients</h2><p><strong>Pour 3/4 personnes</strong></p><ul><li>300g de riz arborio ou carnaroli (riz à risotto)</li><li>1 botte d&#39;asperges verte</li><li>3 ou 4 échalotes (ou 1 oignon)</li><li>100g de parmesan</li><li>1.4L de bouillon (cube de poulet ou légumes)</li><li>1 orange pressée</li><li>1 verre de vin blanc</li><li>1 gousse d&#39;ail</li><li>50g de beurre</li><li>Huile d&#39;olive</li></ul><h2 id="preparation-20-minutes" tabindex="-1"><a class="header-anchor" href="#preparation-20-minutes" aria-hidden="true">#</a> Préparation (20 minutes)</h2><p><strong>Asperges (15/20 minutes)</strong></p><ul><li>Faire bouillir de l&#39;eau.</li><li>Couper les têtes des asperges et réserver-les.</li><li>Briser le reste de l&#39;asperge à la main (les asperges se cassent naturellement au niveau de la partie la plus dure).</li><li>Faire cuire les têtes d&#39;asperges dans l&#39;eau bouillante <strong>6 minutes</strong>.</li><li>Retirer les feuilles des tiges et couper les tiges en rondelles fines.</li><li>A la fin de la cuisson des asperges les plonger de l&#39;eau très froide pour stopper la cuisson et conserver leur couleur verte.</li></ul><p><strong>Échalotes</strong></p><p>Ciseler vos échalotes.</p><p><strong>Orange</strong></p><p>Presser votre jus d&#39;orange.</p><h2 id="cuisson-35-minutes" tabindex="-1"><a class="header-anchor" href="#cuisson-35-minutes" aria-hidden="true">#</a> Cuisson (35 minutes)</h2><ul><li>Dans une sauteuse, faire chauffer un bon gros filet d&#39;huile. L&#39;huile doit être très chaude.</li><li>Faire revenir les échalotes tout en remuant, pour qu&#39;elles soient juste blanchies.</li><li>Ajouter le riz et remuer, pour faire éclater le riz, jusqu&#39;à ce qu&#39;il soit transparent. Ajouter de l&#39;huile si nécessaire.</li><li>Déglacer avec le verre de vin blanc (on doit entendre un son assez fort). Et mélanger jusqu&#39;à son evaporation.</li><li>Une fois évaporé, ajouter le jus d&#39;orange et mélanger jusqu&#39;à son évaporation également.</li><li>Ajouter l&#39;ail entier.</li><li>Mouiller ensuite votre riz avec 2 ou 3 louches de bouillon, et mélanger.</li><li>Une fois le bouillon absorbé, ajouter de nouveau 2 louches, mélanger et répéter l&#39;opération jusqu&#39;à ce qu&#39;il reste 5 louches de bouillon.</li><li>Incorporer les rondelles d&#39;asperges avec le riz, et ajouter 2 louches de bouillons tout en mélangeant.</li><li>Ajouter encore 2 louches (il ne doit en rester plus qu&#39;une) et continuer de mélanger.</li><li>Ajouter les 50 g de beurre et mélanger.</li><li>Ajouter ensuite le reste du bouillon et attendez son absorption.</li><li>Ajouter les têtes d&#39;asperges au moment de servir, et <strong>c&#39;est prêt !</strong></li></ul>',15),o=[t];function a(n,u){return r(),s("div",null,o)}const p=e(l,[["render",a],["__file","2020-05-22_risotto-aux-asperges-vertes.html.vue"]]);export{p as default};
